import "./DragDrop.css";
import { fabric } from "fabric";

import { connect } from "react-redux";
import React, { Component } from "react";

import DrawText from "../DrawText/DrawText";
import Images from "../Image/Images";
import FontFamily from "../Fonts/FontFamily/FontsFamily";
import Input from "../Input/Input";
import TextAlign from "../Fonts/TextAlign/TextAlign";
import Download from "../Download/Download";
import Group from "../GroupUngroup/Group";
import Ungroup from "../GroupUngroup/Ungroup";
import MultipleSelection from "../MultipleSelection/MultipleSelection";
import Backward from "../Position/Backward/Backward";
import Forward from "../Position/Forward/Forward";

class DragDrop extends Component {
  constructor(props) {
    super(props);
    this.canvasRef = React.createRef();
    this.canvas = "";
    fabric.Object.prototype.set({
      borderColor: "#53c5bf",
      cornerColor: "#acdab5b8",
      cornerSize: 10,
      padding: 1,
      hasBorders: false,
      rotatingPointOffset: 20,
      cornerStyle: "circle"
      // transparentCorners: true
    });
  }

  state = {
    canvas: "",
    selectedImg: "",
    selectedColor: "#000000",
    lineHeight: 1.16,
    transparent: 100,
    letterSpacing: 10,
    selectedBackGroundColor: "#77a05f",
    selectedFontFamily: "Helvetica Neue",
    fontSize: 30,
    copyPasteVisibility: false,
    textVisibility: false,
    selectedTextAlign: "",
    fontFamilys: [
      "arial",
      "optima",
      "hoefler text",
      "plaster",
      "engagement",
      "VT323",
      "Helvetica Neue",
      "myriad pro",
      "comic sans ms",
      "delicious",
      "verdana",
      "georgia",
      "courier",
      "impact",
      "monaco"
    ],
    textAligns: ["left", "right", "center", "justify"],
    imgSrc: [
      "1.jpg",
      "2.jpg",
      "3.jpg",
      "4.jpg",
      "5.png",
      "6.jpg",
      "7.jpg",
      "8.jpg",
      "9.jpg",
      "10.png",
      "11.jpg",
      "12.jpg",
      "13.jpg",
      "14.jpg"
    ],
    cloneObject: ""
  };

  onDragStart = event => {
    this.setState({ selectedImg: event.target });
  };

  drawImage = () => {
    fabric.Image.fromURL(this.state.selectedImg.src, img => {
      img.set({
        left: 0,
        top: 0

        // border: "#000",
        // stroke: "#F0F0F0",
        // strokeWidth: 5
      });
      img.scaleToHeight(500);
      img.scaleToWidth(500);
      this.canvas.add(img).renderAll();
    });
  };

