  return { imgSrcData: state.getImagesSrc, fontListData: state.getFontList };
};

const mapDispatchToProps = {
  onGetImageSrc: getImageSrc,
  onGetFontsList: getFontsList
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(DragDrop);

